mapper.insert:
insert into sys_application

	ID

		,NAME


		,CODE


		,APPDESC


		,URL


		,SORT


		,SHOWMENU


		,NODEID




	#{id, jdbcType=BIGINT}


		,#{name, jdbcType=VARCHAR}


		,#{code, jdbcType=VARCHAR}


		,#{desc, jdbcType=VARCHAR}


		,#{url, jdbcType=VARCHAR}


		,#{sort, jdbcType=INTEGER}


		,#{showMenu, jdbcType=INTEGER}


		,#{nodeId, jdbcType=BIGINT}

mapper.delete:
delete from sys_application
where (
ID IN

	#{x_rowId}

)

mapper.select:
select E.*



		order by ${orderBy}


		order by E.SORT asc

mapper.sql:
from sys_application E


	inner join sys_tree T
	on E.NODEID = T.ID



	1 = 1


		and T.PARENT = #{parentId}



		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and E.NAME IN

			#{x_name}




		and E.CODE like #{codeLike}



		and E.APPDESC like #{descLike}



		and E.URL like #{urlLike}



		and E.SORT >= #{sortGreaterThanOrEqual}



		and E.SORT > #{sortGreaterThan}



		and E.SORT <= #{sortLessThanOrEqual}



		and E.SORT < #{sortLessThan}



		and E.SHOWMENU = #{showMenu}



		and E.SHOWMENU >= #{showMenuGreaterThanOrEqual}



		and E.SHOWMENU <= #{showMenuLessThanOrEqual}



		and E.NODEID = #{nodeId}



		and E.NODEID >= #{nodeIdGreaterThanOrEqual}



		and E.NODEID <= #{nodeIdLessThanOrEqual}



		and E.NODEID IN

			#{x_nodeId}
