mapper.insert:
insert into SYS_APPLICATION

	ID

		,NAME


		,TYPE


		,CODE


		,APPDESC


		,URL


		,SORT


		,SHOWMENU


		,NODEID


		,LOCKED


		,OPENFLAG


		,CREATEDATE


		,CREATEBY


		,UPDATEBY


		,UPDATEDATE




	#{id, jdbcType=BIGINT}


		,#{name, jdbcType=VARCHAR}


		,#{type, jdbcType=VARCHAR}


		,#{code, jdbcType=VARCHAR}


		,#{desc, jdbcType=VARCHAR}


		,#{url, jdbcType=VARCHAR}


		,#{sort, jdbcType=INTEGER}


		,#{showMenu, jdbcType=INTEGER}


		,#{nodeId, jdbcType=BIGINT}


		,#{locked, jdbcType=INTEGER}


		,#{openFlag, jdbcType=VARCHAR}


		,#{createDate, jdbcType=TIMESTAMP}


		,#{createBy, jdbcType=VARCHAR}


		,#{updateBy, jdbcType=VARCHAR}


		,#{updateDate, jdbcType=TIMESTAMP}

mapper.delete:
delete from SYS_APPLICATION
where (
ID IN

	#{x_rowId}

)

mapper.select:
select E.*



		order by ${orderBy}


		order by E.SORT asc

mapper.sql:
from SYS_APPLICATION E


	inner join SYS_TREE T
	on E.NODEID = T.ID



	1 = 1


		and T.PARENT = #{parentId}



		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and E.NAME IN

			#{x_name}




		and E.TYPE = #{type}



		and E.CODE = #{code}



		and E.CODE like #{codeLike}



		and E.APPDESC like #{descLike}



		and E.URL like #{urlLike}



		and E.SORT >= #{sortGreaterThanOrEqual}



		and E.SORT > #{sortGreaterThan}



		and E.SORT <= #{sortLessThanOrEqual}



		and E.SORT < #{sortLessThan}



		and E.SHOWMENU = #{showMenu}



		and E.OPENFLAG = #{openFlag}



		and E.NODEID = #{nodeId}



		and E.NODEID IN

			#{x_nodeId}




		and E.LOCKED is not null
		and E.LOCKED = #{locked}
