mapper.insert:
insert into Message 

    ID

		,TYPE 


		,SYSTYPE 


		,RECVERLIST 


		,TITLE 


		,CONTENT 


		,CREATEDATE 


		,READED 


		,CATEGORY 


		,SENDER 


		,RECVER 




	  #{id, jdbcType=BIGINT}


		,#{type, jdbcType=INTEGER}


		,#{sysType, jdbcType=INTEGER}


		,#{recverList, jdbcType=VARCHAR}


		,#{title, jdbcType=VARCHAR}


		,#{content, jdbcType=VARCHAR}


		,#{createDate, jdbcType=TIMESTAMP}


		,#{readed, jdbcType=INTEGER}


		,#{category, jdbcType=INTEGER}


		,#{senderId, jdbcType=BIGINT}


		,#{recverId, jdbcType=BIGINT}

mapper.delete:
		delete from Message
		where ( 
 			  ID IN

                  #{x_rowId}

		)

mapper.select:
select E.*



     order by ${orderBy}


      order by E.ID desc

mapper.sql:
		from Message E


		       1 = 1  


				and E.TYPE = #{type}



				and E.TYPE >= #{typeGreaterThanOrEqual}



				and E.TYPE <= #{typeLessThanOrEqual}



			    and E.TYPE IN

                  #{x_type}





				and E.SYSTYPE = #{sysType}



				and E.SYSTYPE >= #{sysTypeGreaterThanOrEqual}



				and E.SYSTYPE <= #{sysTypeLessThanOrEqual}



			    and E.SYSTYPE IN

                  #{x_sysType}




				and E.RECVERLIST like #{recverListLike}



				and E.TITLE like #{titleLike}



				and E.CONTENT like #{contentLike}



				and E.CREATEDATE >= #{createDateGreaterThanOrEqual}



				and E.CREATEDATE <= #{createDateLessThanOrEqual}



				and E.READED = #{readed}



				and E.READED >= #{readedGreaterThanOrEqual}



				and E.READED <= #{readedLessThanOrEqual}



				and E.CATEGORY = #{category}



				and E.CATEGORY >= #{categoryGreaterThanOrEqual}



				and E.CATEGORY <= #{categoryLessThanOrEqual}



			    and E.CATEGORY IN

                  #{x_category}





				and E.SENDER = #{senderId}



				and E.SENDER >= #{senderIdGreaterThanOrEqual}



				and E.SENDER <= #{senderIdLessThanOrEqual}



			    and E.SENDER IN

                  #{x_senderId}




				and E.RECVER = #{recverId}



				and E.RECVER >= #{recverIdGreaterThanOrEqual}



				and E.RECVER <= #{recverIdLessThanOrEqual}



			    and E.RECVER IN

                  #{x_recverId}
