mapper.insert:
		insert into
		SYS_CHART ( ID_
        ,QUERYIDS_ 
        ,QUERYSQL_ 
        ,SUBJECT_ 
        ,CHARTNAME_ 
        ,CHARTTITLE_ 
        ,CHARTTYPE_ 
        ,CHARTFONT_ 
        ,CHARTFONTSIZE_ 
        ,CHARTTITLEFONT_ 
        ,CHARTTITLEFONTSIZE_ 
        ,CHARTWIDTH_ 
        ,CHARTHEIGHT_ 
        ,MAPPING_
        ,IMAGETYPE_ 
        ,LEGEND_ 
        ,TOOLTIP_ 
        ,COORDINATEX_ 
        ,COORDINATEY_ 
        ,PLOTORIENTATION_ 
        ,ENABLEFLAG_
        ,CREATEDATE_ 
        ,CREATEBY_ 
		)
		values (
          #{id, jdbcType=VARCHAR}
        ,#{queryIds, jdbcType=VARCHAR}
        ,#{querySQL, jdbcType=VARCHAR}
        ,#{subject, jdbcType=VARCHAR}
        ,#{chartName, jdbcType=VARCHAR}
        ,#{chartTitle, jdbcType=VARCHAR}
        ,#{chartType, jdbcType=VARCHAR}
        ,#{chartFont, jdbcType=VARCHAR}
        ,#{chartFontSize, jdbcType=INTEGER}
        ,#{chartTitleFont, jdbcType=VARCHAR}
        ,#{chartTitleFontSize, jdbcType=INTEGER}
        ,#{chartWidth, jdbcType=INTEGER}
        ,#{chartHeight, jdbcType=INTEGER}
        ,#{mapping, jdbcType=VARCHAR}
        ,#{imageType, jdbcType=VARCHAR}
        ,#{legend, jdbcType=VARCHAR}
        ,#{tooltip, jdbcType=VARCHAR}
        ,#{coordinateX, jdbcType=VARCHAR}
        ,#{coordinateY, jdbcType=VARCHAR}
        ,#{plotOrientation, jdbcType=VARCHAR}
        ,#{enableFlag, jdbcType=VARCHAR}
        ,#{createDate, jdbcType=TIMESTAMP}
        ,#{createBy, jdbcType=VARCHAR}
		)

mapper.update:
update SYS_CHART set
QUERYIDS_ = #{queryIds, jdbcType=VARCHAR}, QUERYSQL_ = #{querySQL, jdbcType=VARCHAR}, SUBJECT_ = #{subject, jdbcType=VARCHAR}, CHARTNAME_ = #{chartName, jdbcType=VARCHAR}, CHARTTITLE_ = #{chartTitle, jdbcType=VARCHAR}, CHARTTYPE_ = #{chartType, jdbcType=VARCHAR}, CHARTFONT_ = #{chartFont, jdbcType=VARCHAR}, CHARTFONTSIZE_ = #{chartFontSize, jdbcType=INTEGER}, CHARTTITLEFONT_ = #{chartTitleFont, jdbcType=VARCHAR}, CHARTTITLEFONTSIZE_ = #{chartTitleFontSize, jdbcType=INTEGER}, CHARTWIDTH_ = #{chartWidth, jdbcType=INTEGER}, CHARTHEIGHT_ = #{chartHeight, jdbcType=INTEGER}, MAPPING_ = #{mapping, jdbcType=VARCHAR}, IMAGETYPE_ = #{imageType, jdbcType=VARCHAR}, LEGEND_ = #{legend, jdbcType=VARCHAR}, TOOLTIP_ = #{tooltip, jdbcType=VARCHAR}, COORDINATEX_ = #{coordinateX, jdbcType=VARCHAR}, COORDINATEY_ = #{coordinateY, jdbcType=VARCHAR}, PLOTORIENTATION_ = #{plotOrientation, jdbcType=VARCHAR}, ENABLEFLAG_ = #{enableFlag, jdbcType=VARCHAR},
where ID_ = #{id, jdbcType=VARCHAR}

mapper.delete:
		delete from SYS_CHART
		where ( 
 			  ID_ IN

                  #{x_rowId}

		)

mapper.sql:
		from SYS_CHART E


		       1 = 1  


			    and E.ID_ IN

                  #{x_chart}




				and E.ENABLEFLAG_ = #{enableFlag}



				and E.SUBJECT_ like #{subjectLike}



				and E.CHARTNAME_ = #{chartName}



				and E.CHARTNAME_ like #{chartNameLike}



				and E.CHARTTITLE_ like #{chartTitleLike}



				and E.CHARTTYPE_ = #{chartType}



				and E.CHARTTYPE_ like #{chartTypeLike}



				and E.IMAGETYPE_ = #{imageType}



				and E.IMAGETYPE_ like #{imageTypeLike}



			    and E.IMAGETYPE_ IN

                  #{x_imageType}




				and E.CREATEDATE_ >= #{createDateGreaterThanOrEqual}



				and E.CREATEDATE_ <= #{createDateLessThanOrEqual}



				and E.CREATEBY_ = #{createBy}



				and E.CREATEBY_ like #{createByLike}
