,PRIORITY_
,CREATETIME_
,CREATEBY_
,LOCKED_
,REVISION_
)
values (
#{id, jdbcType=VARCHAR}

,#{activityId, jdbcType=VARCHAR}
,#{queryId, jdbcType=VARCHAR}
,#{title, jdbcType=VARCHAR}
,#{description, jdbcType=VARCHAR}
,#{priority, jdbcType=INTEGER}
,#{createTime, jdbcType=TIMESTAMP}
,#{createBy, jdbcType=VARCHAR}
,#{locked, jdbcType=INTEGER}
,#{revision, jdbcType=INTEGER}
)

mapper.update:
update SYS_DTS_STEP set
ACTIVITYID_ = #{activityId, jdbcType=VARCHAR}, QUERYID_ = #{queryId, jdbcType=VARCHAR}, TITLE_ = #{title, jdbcType=VARCHAR}, DESCRIPTION_ = #{description, jdbcType=VARCHAR}, PRIORITY_ = #{priority, jdbcType=INTEGER}, LOCKED_ = #{locked, jdbcType=INTEGER}, REVISION_ = #{revision, jdbcType=INTEGER},
where ID_ = #{id, jdbcType=VARCHAR}

mapper.delete:
delete from SYS_DTS_STEP
where (
ID_ IN

	#{x_rowId}

)

mapper.sql:
from SYS_DTS_STEP E


	1 = 1


		and E.ACTIVITYID_ = #{activityId}



		and E.ACTIVITYID_ IN

			#{x_activityId}




		and E.QUERYID_ = #{queryId}



		and E.TITLE_ like #{titleLike}



		and E.DESCRIPTION_ like #{descriptionLike}



		and E.PRIORITY_ = #{priority}



		and E.PRIORITY_ >= #{priorityGreaterThanOrEqual}



		and E.PRIORITY_ <= #{priorityLessThanOrEqual}



		and E.CREATETIME_ >= #{createTimeGreaterThanOrEqual}



		and E.CREATETIME_ <= #{createTimeLessThanOrEqual}



		and E.CREATEBY_ = #{createBy}



		and E.LOCKED_ = #{locked}



		and E.LOCKED_ >= #{lockedGreaterThanOrEqual}



		and E.LOCKED_ <= #{lockedLessThanOrEqual}



		and E.REVISION_ = #{revision}



		and E.REVISION_ >= #{revisionGreaterThanOrEqual}



		and E.REVISION_ <= #{revisionLessThanOrEqual}
