mapper.update:
update SYS_LOB
set BUSINESSKEY_ = #{businessKey, jdbcType=VARCHAR},
LOCKED_ = #{locked, jdbcType=INTEGER},
STATUS_ = #{status, jdbcType=INTEGER}
where ID_ = #{id,jdbcType=VARCHAR}

mapper.delete:
delete from SYS_LOB
where (

	 OR 
	ID_ = #{rowId}

)

mapper.sql:
from SYS_LOB E


	1 = 1


		and E.DEVICEID_ = #{deviceId}



		and E.BUSINESSKEY_ = #{businessKey}



		and E.BUSINESSKEY_ IN

			#{x_businessKey}




		and E.FILEID_ = #{fileId}



		and E.SERVICEKEY_ = #{serviceKey}



		and E.NAME_ = #{name}



		and E.NAME_ like #{nameLike}



		and E.NAME_ IN

			#{x_name}




		and E.TYPE_ = #{type}



		and E.FILENAME_ = #{filename}



		and E.FILENAME_ like #{filenameLike}



		and E.CONTENTTYPE_ = #{contentType}



		and E.OBJECTID_ = #{objectId}



		and E.OBJECTID_ IN

			#{x_objectId}




		and E.OBJECTVALUE_ = #{objectValue}



		and E.OBJECTVALUE_ IN

			#{x_objectValue}




		and E.SIZE_ >= #{sizeGreaterThanOrEqual}



		and E.SIZE_ <= #{sizeLessThanOrEqual}



		and E.LASTMODIFIED_ >=
		#{lastModifiedGreaterThanOrEqual}



		and E.LASTMODIFIED_ <= #{lastModifiedLessThanOrEqual}



		and E.LOCKED_ = #{locked}



		and E.STATUS_ = #{status}



		and E.DELETEFLAG_ = #{deleteFlag}



		and E.CREATEBY_ = #{createBy}



		and E.CREATEDATE_ >= #{createDateGreaterThanOrEqual}



		and E.CREATEDATE_ <= #{createDateLessThanOrEqual}
