mapper.delete:
		delete from oa_overtime
		where ( 
 			  ID_ IN

                  #{x_id}

		)

mapper.select:
select E.*



     order by ${orderBy}


      order by E.ID_ desc

mapper.sql:
		from oa_overtime E



		       1 = 1  



				and E.overtimeid = #{overtimeid}



				and E.overtimeid >= #{overtimeidGreaterThanOrEqual}



				and E.overtimeid <= #{overtimeidLessThanOrEqual}



			    and E.overtimeid IN

                  #{x_overtimeid}





				and E.area = #{area}



				and E.area like #{areaLike}



			    and E.area IN

                  #{x_area}





				and E.company = #{company}



				and E.company like #{companyLike}



			    and E.company IN

                  #{x_company}




				and E.dept = #{dept}



				and E.dept like #{deptLike}



			    and E.dept IN

                  #{x_dept}




				and E.appuser = #{appuser}



				and E.appuser like #{appuserLike}



			    and E.appuser IN

                  #{x_appuser}




				and E.post = #{post}



				and E.post like #{postLike}



			    and E.post IN

                  #{x_post}




				and E.appdate = #{appdate}



				and E.appdate >= #{appdateGreaterThanOrEqual}



				and E.appdate <= #{appdateLessThanOrEqual}



			    and E.appdate IN

                  #{x_appdate}





				and E.type = #{type}



				and E.type >= #{typeGreaterThanOrEqual}



				and E.type <= #{typeLessThanOrEqual}



			    and E.type IN

                  #{x_type}





				and E.startdate = #{startdate}



				and E.startdate >= #{startdateGreaterThanOrEqual}



				and E.startdate <= #{startdateLessThanOrEqual}



			    and E.startdate IN

                  #{x_startdate}





				and E.starttime = #{starttime}



				and E.starttime >= #{starttimeGreaterThanOrEqual}



				and E.starttime <= #{starttimeLessThanOrEqual}



			    and E.starttime IN

                  #{x_starttime}





				and E.enddate = #{enddate}



				and E.enddate >= #{enddateGreaterThanOrEqual}



				and E.enddate <= #{enddateLessThanOrEqual}



			    and E.enddate IN

                  #{x_enddate}





				and E.endtime = #{endtime}



				and E.endtime >= #{endtimeGreaterThanOrEqual}



				and E.endtime <= #{endtimeLessThanOrEqual}



			    and E.endtime IN

                  #{x_endtime}





				and E.overtimesum = #{overtimesum}



				and E.overtimesum >= #{overtimesumGreaterThanOrEqual}



				and E.overtimesum <= #{overtimesumLessThanOrEqual}



			    and E.overtimesum IN

                  #{x_overtimesum}





				and E.status = #{status}



				and E.status >= #{statusGreaterThanOrEqual}



				and E.status <= #{statusLessThanOrEqual}



			    and E.status IN

                  #{x_status}





				and E.processname = #{processname}



				and E.processname like #{processnameLike}



			    and E.processname IN

                  #{x_processname}




				and E.processinstanceid = #{processinstanceid}



			    and E.processinstanceid IN

                  #{x_processinstanceid}





				and E.wfstatus = #{wfstatus}



				and E.wfstatus >= #{wfstatusGreaterThanOrEqual}



				and E.wfstatus <= #{wfstatusLessThanOrEqual}



			    and E.wfstatus IN

                  #{x_wfstatus}




				and E.createDate = #{createDate}



				and E.createDate >= #{createDateGreaterThanOrEqual}



				and E.createDate <= #{createDateLessThanOrEqual}



			    and E.createDate IN

                  #{x_createDate}





				and E.updateDate = #{updateDate}



				and E.updateDate >= #{updateDateGreaterThanOrEqual}



				and E.updateDate <= #{updateDateLessThanOrEqual}



			    and E.updateDate IN

                  #{x_updateDate}




				and E.createBy = #{createBy}



				and E.createBy like #{createByLike}



			    and E.createBy IN

                  #{x_createBy}




				and E.updateBy = #{updateBy}



				and E.updateBy like #{updateByLike}



			    and E.updateBy IN

                  #{x_updateBy}




				and (E.createBy = #{createByAndApply} or E.appuser = #{createByAndApply})

mapper.select:
select E.*



		order by ${orderBy}


		order by E.id_ desc

mapper.sql:
		from oa_overtime E


			inner join JBPM_TASKINSTANCE T on cast(E.PROCESSINSTANCEID as
			integer) = T.PROCINST_



			1 = 1	

				and ( T.END_ IS  NULL)
				and ( T.ACTORID_ IN

					#{x_actorId}

				)	



				and cast(E.PROCESSINSTANCEID as integer) in (
				SELECT a.PROCINST_
				FROM JBPM_TASKINSTANCE a
				WHERE (1 = 1)
				AND (a.END_ IS not NULL)
				AND (a.ACTORID_ IS NOT NULL)
				AND (a.ACTORID_ in

					#{x_actorId1}

				)
				union
				SELECT a.PROCINST_
				FROM JBPM_POOLEDACTOR p
				INNER JOIN JBPM_TASKACTORPOOL t
				ON p.ID_ = t.POOLEDACTOR_
				INNER JOIN JBPM_TASKINSTANCE a
				ON t.TASKINSTANCE_ = a.ID_
				WHERE (1 = 1)
				AND (a.END_ IS NULL)
				AND (a.ACTORID_ IS NULL)
				AND (p.ACTORID_ IN 

					#{x_actorId2}

				)
				union 
				select distinct 0 from JBPM_TASKINSTANCE 
				)




				and E.area = #{area}




				and E.company = #{company}



				and E.dept = #{dept}



				and E.appuser = #{appuser}



				and E.appuser like #{appuserLike}



				and E.post = #{post}



				and E.post like #{postLike}



				and E.appdate >= #{appdateGreaterThanOrEqual}



				and E.appdate <= #{appdateLessThanOrEqual}




				and E.type = #{type}



				and E.status = #{status}



				and E.status >= #{statusGreaterThanOrEqual}



				and E.status <= #{statusLessThanOrEqual}



			    and E.status IN

                  #{x_status}
