mapper.insert:
insert into sys_tree

	ID

		,PARENT


		,NAME


		,NODEDESC


		,DISCRIMINATOR


		,TREEID


		,SORT


		,CODE


		,icon


		,iconCls


		,url


		,locked


		,CREATEDATE 


		,CREATEBY 


		,UPDATEBY 


		,UPDATEDATE 




	#{id, jdbcType=BIGINT}


		,#{parentId, jdbcType=BIGINT}


		,#{name, jdbcType=VARCHAR}


		,#{desc, jdbcType=VARCHAR}


		,#{discriminator, jdbcType=VARCHAR}


		,#{treeId, jdbcType=VARCHAR}


		,#{sort, jdbcType=INTEGER}


		,#{code, jdbcType=VARCHAR}


		,#{icon, jdbcType=VARCHAR}


		,#{iconCls, jdbcType=VARCHAR}


		,#{url, jdbcType=VARCHAR}


		,#{locked, jdbcType=INTEGER}


		,#{createDate, jdbcType=TIMESTAMP}


		,#{createBy, jdbcType=VARCHAR}


		,#{updateBy, jdbcType=VARCHAR}


		,#{updateDate, jdbcType=TIMESTAMP}

mapper.select:
select E.* from sys_tree E


	1 = 1


		and E.PARENT = #{parentId}



		and E.PARENT IN

			#{x_parentId}




		and E.DISCRIMINATOR = #{discriminator}



		and E.NAME = #{name}



		and E.NAME like #{nameLike}



		and E.NODEDESC like #{descLike}



		and E.SORT >= #{sortGreaterThanOrEqual}



		and E.SORT > #{sortGreaterThan}



		and E.SORT <= #{sortLessThanOrEqual}



		and E.SORT < #{sortLessThan}



		and E.CODE = #{code}



		and E.CODE like #{codeLike}



		and E.CODE IN

			#{x_code}




		and E.locked = #{locked}



		and E.locked >= #{lockedGreaterThanOrEqual}



		and E.locked <= #{lockedLessThanOrEqual}






		order by ${orderBy}


		order by E.SORT asc

 SELECT u.account as actorId
   FROM sys_user u
   WHERE u.blocked = 0 AND u.id IN
		 ( SELECT r.userId  FROM sys_user_role r
	       WHERE r.authorized = 0 AND r.roleId IN
				 ( SELECT a.id
			       FROM sys_dept_role a, sys_role b
			       WHERE a.sysRoleId = b.id


	                 AND a.deptId = #{nodeId} 



	                 AND b.code = #{roleId} 



					AND b.code IN

						#{x_roleId}



				 )
		)

SELECT u.* FROM sys_user u WHERE u.blocked = 0 AND u.id IN ( SELECT r.userId FROM sys_user_role r WHERE r.authorized = 0 AND r.roleId IN ( SELECT a.id FROM sys_dept_role a, sys_role b WHERE 1=1 AND a.sysRoleId = b.id
AND b.code= #{roleId}
AND b.code IN
#{x_roleId}
AND ( a.deptId = #{nodeId} )
AND a.deptId IN
#{x_deptId}
) )

SELECT u.* FROM sys_user u WHERE u.blocked = 0 AND u.id IN ( SELECT r.userId FROM sys_user_role r WHERE r.authorized = 0 AND r.roleId IN ( SELECT a.id FROM sys_dept_role a, sys_role b WHERE 1=1 AND a.sysRoleId = b.id
AND b.code= #{roleId}
AND b.code IN
#{x_roleId}
AND ( a.deptId = #{nodeId} )
AND a.deptId IN
#{x_deptId}
) )

SELECT r.*
FROM sys_role r
WHERE r.id = #{roleId}

SELECT r.*
FROM sys_role r
WHERE r.code = #{code}

SELECT u.* 
FROM sys_user u
WHERE 1=1 
AND (
   u.account LIKE #{searchWord}
   OR 
   u.name LIKE #{searchWord}
   OR
   u.email LIKE #{searchWord}
   OR
   u.mobile LIKE #{searchWord}
)

ORDER BY u.name ASC

select distinct r.CODE from SYS_ROLE r inner join SYS_DEPT_ROLE dr on r.ID = dr.SYSROLEID inner join SYS_USER_ROLE ur on dr.ID = ur.ROLEID inner join SYS_USER u on ur.USERID = u.ID where r.CODE is not null
and u.account = #{actorId}
and u.account IN
#{x_actorId}
and ( u.deptid = #{nodeId} )
and u.deptid IN
#{x_nodeId}
and 1=0
union
select E.code from sys_application E inner join sys_access A on E.id = A.appId where E.code is not null and A.roleId in ( select dr.id from sys_dept_role dr inner join sys_user_role ur on dr.id = ur.roleid inner join sys_user u on ur.userid = u.id where ur.authorized = 0
and u.account = #{actorId}
and u.account IN
#{x_actorId}
and ( u.deptid = #{nodeId} )
and u.deptid IN
#{x_nodeId}
and 1=0
)
union
select E.CODE from sys_function E inner join sys_permission A on E.id = A.funcId where E.CODE is not null and A.roleId in ( select dr.id from sys_dept_role dr inner join sys_user_role ur on dr.id = ur.roleid inner join sys_user u on u.id = ur.userid where ur.authorized = 0
and u.account = #{actorId}
and u.account IN
#{x_actorId}
and ( u.deptid = #{nodeId} )
and u.deptid IN
#{x_nodeId}
and 1=0
)

SELECT u.*
FROM sys_user u
WHERE 1=1 



		and u.account = #{activationCode} 


		and u.account = #{actorId} 


		and u.account IN

			#{x_actorId}



		AND ( u.deptid = #{deptId} ) 


		AND u.deptid IN

			#{x_deptId}






ORDER BY u.name ASC

SELECT u.*
FROM sys_user u 
WHERE u.account = #{actorId}
