mapper.select:
 SELECT u.account as actorId
   FROM sys_user u
   WHERE u.blocked = 0 AND u.id IN
		 ( SELECT r.userId  FROM sys_user_role r
	       WHERE r.authorized = 0 AND r.roleId IN
				 ( SELECT a.id
			       FROM sys_dept_role a, sys_role b
			       WHERE a.sysRoleId = b.id


	                 AND a.deptId = #{nodeId} 



	                 AND b.code = #{roleId} 



					AND b.code IN

						#{x_roleId}



				 )
		)

SELECT u.* FROM sys_user u WHERE u.blocked = 0 AND u.id IN ( SELECT r.userId FROM sys_user_role r WHERE r.authorized = 0 AND r.roleId IN ( SELECT a.id FROM sys_dept_role a, sys_role b WHERE 1=1 AND a.sysRoleId = b.id
AND b.code= #{roleId}
AND b.code IN
#{x_roleId}
AND ( a.deptId = #{nodeId} )
AND a.deptId IN
#{x_deptId}
) )

SELECT u.* FROM sys_user u WHERE u.blocked = 0 AND u.id IN ( SELECT r.userId FROM sys_user_role r WHERE r.authorized = 0 AND r.roleId IN ( SELECT a.id FROM sys_dept_role a, sys_role b WHERE 1=1 AND a.sysRoleId = b.id
AND b.code= #{roleId}
AND b.code IN
#{x_roleId}
AND ( a.deptId = #{nodeId} )
AND a.deptId IN
#{x_deptId}
) )

SELECT r.*
FROM sys_role r
WHERE r.id = #{roleId}

SELECT r.*
FROM sys_role r
WHERE r.code = #{code}

SELECT x.* 
FROM sys_user x
WHERE 1=1 
AND (
   x.account LIKE #{searchWord}
   OR 
   x.name LIKE #{searchWord}
   OR
   x.email LIKE #{searchWord}
   OR
   x.mobile LIKE #{searchWord}
)

ORDER BY x.name ASC

SELECT x.*
FROM sys_user x
WHERE 1=1 



		and x.account = #{activationCode} 


		and x.account = #{actorId} 


		and x.account IN

			#{x_actorId}



		AND ( x.deptid = #{deptId} ) 


		AND x.deptid IN

			#{x_deptId}






ORDER BY x.name ASC

SELECT x.*
FROM sys_user x 
WHERE x.account = #{actorId}
