mapper.insert:
		insert into
		X_APP_TRIP ( ID_
        ,TRANSTYPE_ 
        ,APPLYDATE_ 
        ,STARTDATE_ 
        ,ENDDATE_ 
        ,DAYS_ 
        ,MONEY_ 
        ,CAUSE_ 
        ,CREATEDATE_ 
        ,CREATEBY_ 
        ,UPDATEDATE_ 
        ,LOCKED_ 
        ,DELETEFLAG_ 
        ,STATUS_ 
        ,PROCESSNAME_ 
        ,PROCESSINSTANCEID_ 
		)
		values (
          #{id, jdbcType=VARCHAR}

        ,#{transType, jdbcType=VARCHAR}
        ,#{applyDate, jdbcType=TIMESTAMP}
        ,#{startDate, jdbcType=TIMESTAMP}
        ,#{endDate, jdbcType=TIMESTAMP}
        ,#{days, jdbcType=DOUBLE}
        ,#{money, jdbcType=DOUBLE}
        ,#{cause, jdbcType=VARCHAR}
        ,#{createDate, jdbcType=TIMESTAMP}
        ,#{createBy, jdbcType=VARCHAR}
        ,#{updateDate, jdbcType=TIMESTAMP}
        ,#{locked, jdbcType=INTEGER}
        ,#{deleteFlag, jdbcType=INTEGER}
        ,#{status, jdbcType=INTEGER}
        ,#{processName, jdbcType=VARCHAR}
        ,#{processInstanceId, jdbcType=VARCHAR}
		)

mapper.update:
update X_APP_TRIP set
TRANSTYPE_ = #{transType, jdbcType=VARCHAR}, APPLYDATE_ = #{applyDate, jdbcType=TIMESTAMP}, STARTDATE_ = #{startDate, jdbcType=TIMESTAMP}, ENDDATE_ = #{endDate, jdbcType=TIMESTAMP}, DAYS_ = #{days, jdbcType=DOUBLE}, MONEY_ = #{money, jdbcType=DOUBLE}, CAUSE_ = #{cause, jdbcType=VARCHAR},
where ID_ = #{id, jdbcType=VARCHAR}

mapper.delete:
		delete from X_APP_TRIP
		where ( 
 			  ID_ IN

                  #{x_rowId}

		)

mapper.sql:
		from X_APP_TRIP E


           inner join JBPM_TASKINSTANCE T
		   on E.PROCESSINSTANCEID_ = T.PROCINST_



		       1 = 1  


			     and ( T.END_ IS NOT NULL)
                 and ( T.ACTORID in

					#{x_actorId}

				 )



			      and E.PROCESSINSTANCEID_ in (
				          SELECT a.PROCINST_
						  FROM JBPM_TASKINSTANCE a 
						  WHERE (1 = 1) 
						  AND (a.END_ IS NULL)
						  AND (a.ACTORID_ IS NOT NULL) 
						  AND (a.ACTORID_ in 

					              #{x_actorId2}

						  )
				        union
				          SELECT a.PROCINST_
						  FROM JBPM_TASKINSTANCE a 
						  INNER JOIN JBPM_TASKACTORPOOL t
						  ON a.ID_ = t.TASKINSTANCE_
						  INNER JOIN JBPM_POOLEDACTOR p  
						  ON p.ID_ = t.POOLEDACTOR_ 
						  WHERE (1 = 1)  
						  AND (a.END_ IS NULL)
						  AND (a.ACTORID_ IS NULL)
						  AND (p.ACTORID_ in 

					              #{x_actorId3}

						  )  
                 )



			      and E.PROCESSINSTANCEID_ in (
				          SELECT a.PROCINST_
						  FROM JBPM_TASKINSTANCE a 
						  WHERE (1 = 1) 
						  AND (a.ACTORID_ IS NOT NULL) 
						  AND (a.ACTORID_ in 

					              #{x_actorId2}

						  )
				        union
				          SELECT a.PROCINST_
						  FROM JBPM_TASKINSTANCE a 
						  INNER JOIN JBPM_TASKACTORPOOL t
						  ON a.ID_ = t.TASKINSTANCE_
						  INNER JOIN JBPM_POOLEDACTOR p  
						  ON p.ID_ = t.POOLEDACTOR_ 
						  WHERE (1 = 1) 
						  AND (a.ACTORID_ IS NULL)
						  AND (p.ACTORID_ in 

					              #{x_actorId3}

						  )  
                 )





				and E.TRANSTYPE_ = #{transType}



				and E.TRANSTYPE_ like #{transTypeLike}



			    and E.TRANSTYPE_ IN

                  #{x_transType}





				and E.APPLYDATE_ = #{applyDate}



				and E.APPLYDATE_ >= #{applyDateGreaterThanOrEqual}



				and E.APPLYDATE_ <= #{applyDateLessThanOrEqual}



			    and E.APPLYDATE_ IN

                  #{x_applyDate}





				and E.STARTDATE_ = #{startDate}



				and E.STARTDATE_ >= #{startDateGreaterThanOrEqual}



				and E.STARTDATE_ <= #{startDateLessThanOrEqual}



			    and E.STARTDATE_ IN

                  #{x_startDate}





				and E.ENDDATE_ = #{endDate}



				and E.ENDDATE_ >= #{endDateGreaterThanOrEqual}



				and E.ENDDATE_ <= #{endDateLessThanOrEqual}



			    and E.ENDDATE_ IN

                  #{x_endDate}





				and E.DAYS_ = #{days}



				and E.DAYS_ >= #{daysGreaterThanOrEqual}



				and E.DAYS_ <= #{daysLessThanOrEqual}



			    and E.DAYS_ IN

                  #{x_days}





				and E.MONEY_ = #{money}



				and E.MONEY_ >= #{moneyGreaterThanOrEqual}



				and E.MONEY_ <= #{moneyLessThanOrEqual}



			    and E.MONEY_ IN

                  #{x_money}





				and E.CAUSE_ = #{cause}



				and E.CAUSE_ like #{causeLike}



			    and E.CAUSE_ IN

                  #{x_cause}





				and E.CREATEDATE_ = #{createDate}



				and E.CREATEDATE_ >= #{createDateGreaterThanOrEqual}



				and E.CREATEDATE_ <= #{createDateLessThanOrEqual}



			    and E.CREATEDATE_ IN

                  #{x_createDate}





				and E.CREATEBY_ = #{createBy}



				and E.CREATEBY_ like #{createByLike}



			    and E.CREATEBY_ IN

                  #{x_createBy}





				and E.UPDATEDATE_ = #{updateDate}



				and E.UPDATEDATE_ >= #{updateDateGreaterThanOrEqual}



				and E.UPDATEDATE_ <= #{updateDateLessThanOrEqual}



			    and E.UPDATEDATE_ IN

                  #{x_updateDate}





				and E.LOCKED_ = #{locked}



				and E.LOCKED_ >= #{lockedGreaterThanOrEqual}



				and E.LOCKED_ <= #{lockedLessThanOrEqual}



			    and E.LOCKED_ IN

                  #{x_locked}





				and E.DELETEFLAG_ = #{deleteFlag}



				and E.DELETEFLAG_ >= #{deleteFlagGreaterThanOrEqual}



				and E.DELETEFLAG_ <= #{deleteFlagLessThanOrEqual}



			    and E.DELETEFLAG_ IN

                  #{x_deleteFlag}





				and E.STATUS_ = #{status}



				and E.STATUS_ >= #{statusGreaterThanOrEqual}



				and E.STATUS_ <= #{statusLessThanOrEqual}



			    and E.STATUS_ IN

                  #{x_status}





				and E.PROCESSNAME_ = #{processName}



				and E.PROCESSNAME_ like #{processNameLike}



			    and E.PROCESSNAME_ IN

                  #{x_processName}





				and E.PROCESSINSTANCEID_ = #{processInstanceId}



				and E.PROCESSINSTANCEID_ like #{processInstanceIdLike}



			    and E.PROCESSINSTANCEID_ IN

                  #{x_processInstanceId}
